import React from 'react';
import { Task } from '../types';
import { motion } from 'framer-motion';

interface Props {
    tasks: Task[];
    removeTask: (id: number) => void;
}

const FinishedList: React.FC<Props> = ({ tasks, removeTask }) => {
    return (
        <div className="p-4 shadow-lg rounded-lg">
            <h2 className="text-lg font-semibold mb-4">Finished Tasks</h2>
            {tasks.filter(task => task.isFinished).map(task => (
                <motion.div key={task.id} initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="mb-2">
                    {task.title}
                    <button onClick={() => removeTask(task.id)} className="text-red-500 ml-2">‚ùå</button>
                </motion.div>
            ))}
        </div>
    );
};

export default FinishedList;
