import React, { useState } from 'react';
import UnfinishedList from './components/UnfinishedList';
import FinishedList from './components/FinishedList';
import { Task, Joke } from './types';
import './App.css';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [newTaskTitle, setNewTaskTitle] = useState("");
  const [joke, setJoke] = useState<Joke | null>(null);
  const [isLoading, setIsLoading] = useState(false);


  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) {
      return "Good Morning";
    } else if (hour < 18) {
      return "Good Afternoon";
    } else {
      return "Good Evening";
    }
  };

  const today = new Date().toLocaleDateString();
  const greeting = getGreeting();

  const addTask = () => {
    if (!newTaskTitle.trim()) return;
    const newTask = { id: Date.now(), title: newTaskTitle, isFinished: false, isPrioritized: false };
    setTasks([...tasks, newTask]);
    setNewTaskTitle("");
  };

  const toggleFinish = (id: number) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, isFinished: !task.isFinished } : task));
  };

  const removeTask = (id: number) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const prioritizeTask = (id: number) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, isPrioritized: !task.isPrioritized } : task));
  };

  const fetchJoke = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('https://official-joke-api.appspot.com/jokes/random');
      const data = await response.json();
      setJoke(data);
    } catch (error) {
      console.error('Failed to fetch joke:', error);
      setJoke(null);
    } finally {
      setIsLoading(false);
    }
  };


  return (
    <div className="app-container">
      <h1 className="app-title">{greeting}! Today is {today}</h1>
      <div className="task-input-section">
        <input
          type="text"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          placeholder="Add a new task"
          className="task-input"
        />
        <button onClick={addTask} className="add-task-button">
          Add Task
        </button>
      </div>
      <div className="task-lists-container">
        <UnfinishedList tasks={tasks} toggleFinish={toggleFinish} removeTask={removeTask} prioritizeTask={prioritizeTask} />
        <FinishedList tasks={tasks} removeTask={removeTask} />
        <button
          className="take-break-button"
          onClick={fetchJoke}
          disabled={isLoading}
        >
          {isLoading ? 'One Sec' : 'Take A Break'}
        </button>
        {joke && (
          <div className="joke-display">
            <p>{joke.setup}</p>
            <p>{joke.punchline}</p>
          </div>
        )}
        {/* <div className="task-list unfinished-tasks">
          <h2 className="task-list-title">Unfinished Tasks</h2>
          {tasks.filter(task => !task.isFinished).map(task => (
            <motion.div key={task.id} initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="task-item">
              <div className="task-details">
                <input type="checkbox" onChange={() => toggleFinish(task.id)} className="task-checkbox mr-2" />
                <span className={`task-title ${task.isPrioritized ? "priority-task" : ""}`}>
                  {task.title}
                </span>
              </div>
              <div className="task-actions">
                <button onClick={() => prioritizeTask(task.id)} className="prioritize-btn mr-2">⭐</button>
                <button onClick={() => removeTask(task.id)} className="remove-btn">❌</button>
              </div>
            </motion.div>
          ))}
        </div> */}
        {/* <div className="task-list finished-tasks">
          <h2 className="task-list-title">Finished Tasks</h2>
          {tasks.filter(task => task.isFinished).map(task => (
            <motion.div key={task.id} initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="task-item finished-task-item">
              <span className="task-title">{task.title}</span>
              <button onClick={() => removeTask(task.id)} className="remove-btn">❌</button>
            </motion.div>
          ))}
        </div> */}
      </div>
    </div>
  );
};

export default App;
