// UnfinishedList.tsx
import React from 'react';
import { useDraggable } from '@dnd-kit/core';
import { motion } from 'framer-motion';
import { Task } from '../types';

interface Props {
    tasks: Task[];
    toggleFinish: (id: number) => void;
    removeTask: (id: number) => void;
    prioritizeTask: (id: number) => void;
}

const DraggableTask: React.FC<{ task: Task; toggleFinish: Props['toggleFinish']; removeTask: Props['removeTask']; prioritizeTask: Props['prioritizeTask'] }> = ({ task, toggleFinish, removeTask, prioritizeTask }) => {
    const { attributes, listeners, setNodeRef } = useDraggable({
        id: task.id.toString(),
    });

    return (
        <motion.div ref={setNodeRef} {...listeners} {...attributes} initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex items-center justify-between mb-2">
            <div className="flex items-center">
                <input type="checkbox" onChange={() => toggleFinish(task.id)} className="mr-2" />
                <span className={task.isPrioritized ? "text-red-500" : ""}>{task.title}</span>
            </div>
            <div>
                <button onClick={() => prioritizeTask(task.id)} className="text-yellow-500 mr-2">⭐</button>
                <button onClick={() => removeTask(task.id)} className="text-red-500">❌</button>
            </div>
        </motion.div>
    );
};

const UnfinishedList: React.FC<Props> = ({ tasks, toggleFinish, removeTask, prioritizeTask }) => {
    return (
        <div className="p-4 shadow-lg rounded-lg">
            <h2 className="text-lg font-semibold mb-4">Unfinished Tasks</h2>
            {tasks.filter(task => !task.isFinished).map(task => (
                <DraggableTask key={task.id} task={task} toggleFinish={toggleFinish} removeTask={removeTask} prioritizeTask={prioritizeTask} />
            ))}
        </div>
    );
};

export default UnfinishedList;
