import React, { useState } from 'react';
//import UnfinishedList from './components/UnfinishedList';
//import FinishedList from './components/FinishedList';
import { Task } from './types';
import { motion } from 'framer-motion';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [newTaskTitle, setNewTaskTitle] = useState("");

  const today = new Date().toLocaleDateString();

  const addTask = () => {
    if (!newTaskTitle.trim()) return;
    const newTask = { id: Date.now(), title: newTaskTitle, isFinished: false, isPrioritized: false };
    setTasks([...tasks, newTask]);
    setNewTaskTitle("");
  };

  const toggleFinish = (id: number) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, isFinished: !task.isFinished } : task));
  };

  const removeTask = (id: number) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const prioritizeTask = (id: number) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, isPrioritized: !task.isPrioritized } : task));
  };

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-2xl font-bold mb-4">Today is {today}</h1>
      <div className="mb-8">
        <input
          type="text"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          placeholder="Add a new task"
          className="border border-gray-300 p-2 mr-4"
        />
        <button onClick={addTask} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Add Task
        </button>
      </div>
      <div className="flex justify-between space-x-4">
        {/* <UnfinishedList tasks={tasks} toggleFinish={toggleFinish} removeTask={removeTask} prioritizeTask={prioritizeTask} />
        <FinishedList tasks={tasks} removeTask={removeTask} /> */}
        <div className="p-4 shadow-lg rounded-lg">
          <h2 className="text-lg font-semibold mb-4">Unfinished Tasks</h2>
          {tasks.filter(task => !task.isFinished).map(task => (
            <motion.div key={task.id} initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex items-center justify-between mb-2">
              <div className="flex items-center">
                <input type="checkbox" onChange={() => toggleFinish(task.id)} className="mr-2" />
                <span className={task.isPrioritized ? "text-red-500" : ""}>{task.title}</span>
              </div>
              <div>
                <button onClick={() => prioritizeTask(task.id)} className="text-yellow-500 mr-2">⭐</button>
                <button onClick={() => removeTask(task.id)} className="text-red-500">❌</button>
              </div>
            </motion.div>
          ))}
        </div>
        <div className="p-4 shadow-lg rounded-lg">
          <h2 className="text-lg font-semibold mb-4">Finished Tasks</h2>
          {tasks.filter(task => task.isFinished).map(task => (
            <motion.div key={task.id} initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="mb-2">
              {task.title}
              <button onClick={() => removeTask(task.id)} className="text-red-500 ml-2">❌</button>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
