import React, { useState } from 'react';
import { Task } from './types';
import { AnimatePresence, motion } from 'framer-motion';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [newTask, setNewTask] = useState("");

  const addTask = () => {
    if (!newTask.trim()) return;
    const updatedTasks = [...tasks, { id: Date.now(), title: newTask }];
    setTasks(updatedTasks);
    setNewTask("");
  };

  return (
    <div className="App container mx-auto mt-10">
      <input
        className="border border-gray-300 p-2 mr-4"
        type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="Add a new task"
      />
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={addTask}>
        Add Task
      </button>
      <div className="mt-5">
        <AnimatePresence>
          {tasks.map(task => (
            <motion.div
              key={task.id}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="border-b border-gray-300 py-2"
            >
              {task.title}
            </motion.div>
          ))}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default App;
