import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import { Task } from '../types';

interface Props {
    tasks: Task[];
    toggleFinish: (id: number) => void;
    removeTask: (id: number) => void;
    prioritizeTask: (id: number) => void;
}

const UnfinishedList: React.FC<Props> = ({ tasks, toggleFinish, removeTask, prioritizeTask }) => {
    return (
        <Droppable droppableId="unfinishedTasks">
            {(provided) => (
                <div {...provided.droppableProps} ref={provided.innerRef} className="p-4 shadow-lg rounded-lg">
                    <h2 className="text-lg font-semibold mb-4">Unfinished Tasks</h2>
                    {tasks.filter(task => !task.isFinished).map((task, index) => (
                        <Draggable key={task.id} draggableId={task.id.toString()} index={index}>
                            {(provided) => (
                                <div
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                    className="flex items-center justify-between mb-2"
                                >
                                    {/* Task Item Content */}
                                    <div className="flex items-center">
                                        <input type="checkbox" onChange={() => toggleFinish(task.id)} className="mr-2" />
                                        <span className={task.isPrioritized ? "text-red-500" : ""}>{task.title}</span>
                                    </div>
                                    <div>
                                        <button onClick={() => prioritizeTask(task.id)} className="text-yellow-500 mr-2">⭐</button>
                                        <button onClick={() => removeTask(task.id)} className="text-red-500">❌</button>
                                    </div>
                                </div>
                            )}
                        </Draggable>
                    ))}
                    {provided.placeholder}
                </div>
            )}
        </Droppable>
    );
};

export default UnfinishedList;
